import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useForm } from '../../hooks/useForm';
import { registerAuth } from '../store/slices/auth/Thunks';

const Registro2 = () => {
  const dispatch = useDispatch();
  const [users, setUsers] = useState([]); // Nuevo estado para almacenar la lista de usuarios

  const { usuario, email, password, onInputChange, formState } = useForm({
    usuario: 'DD',
    email: 'usuario@example.com',
    password: '123456'
  });

  const onSubmit = async (event) => {
    event.preventDefault();
    console.log(formState);
    await dispatch(registerAuth(email, password, usuario));

    // Después de registrar el usuario, actualiza la lista de usuarios
    setUsers([...users, { usuario, email, password }]);
  };

  return (
    <>
      <h1>Registro</h1>
      <hr />

      <form onSubmit={(event) => onSubmit(event)}>
        <input name='usuario' type="text" onChange={(event) => onInputChange(event)} value={usuario} />
        <input name='email' type="email" onChange={(event) => onInputChange(event)} value={email} />
        <input name='password' type="password" onChange={(event) => onInputChange(event)} value={password} />

        <button type="submit">Registro</button>
      </form>

      {/* Muestra la tabla con la información de los usuarios */}
      {users.length > 0 && (
        <div>
          <h2>Usuarios Registrados</h2>
          <table>
            <thead>
              <tr>
                <th>Usuario</th>
                <th>Email</th>
                <th>Password</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr key={index}>
                  <td>{user.usuario}</td>
                  <td>{user.email}</td>
                  <td>{user.password}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </>
  );
};

export default Registro2;
