const handleChange = (evt) => {
        const { name, value } = evt.target;
        setFormState({
          ...formState,
          [name]: value,
        });
      };

const onInputChange = (evt) => {
    const { name, value} = evt.target;
    setFormState({
    ...formState, 
    [name]: value
    })
}
const onResetForm = () => {
setFormState (initialState)
}
