import "./Css_Styles/Registro_styles.css";
import React, { useState } from 'react';
import RegistroIMG from './Imagenes/RegistroIMG.png';
import { useDispatch } from 'react-redux';
import { useForm } from '../../hooks/useForm';
import { registerAuth } from '../store/slices/auth/Thunks';

const Registro = () => {
  const dispatch = useDispatch();
  const [registrationMessage, setRegistrationMessage] = useState('');
  
  const { usuario, email, password, onInputChange, formState } = useForm({
    usuario: '',
    email: '',
    password: '',
  });

  const onSubmit = async (event) => {
    event.preventDefault();

    // Verificar contraseñas
    if (password.length < 6) {
      setRegistrationMessage('La contraseña debe tener al menos 6 caracteres.');
      return;
    }

    if (password !== formState.password2) {
      setRegistrationMessage('Las contraseñas no coinciden.');
      return;
    }

    try {
      // Intentar registrar el usuario
      await dispatch(registerAuth(email, password));

      // Limpiar los campos y mostrar mensaje de éxito
      setRegistrationMessage('Usuario registrado exitosamente.');
      onInputChange({ target: { name: 'usuario', value: '' } });
      onInputChange({ target: { name: 'email', value: '' } });
      onInputChange({ target: { name: 'password', value: '' } });
      onInputChange({ target: { name: 'password2', value: '' } });
    } catch (error) {
      // Manejar errores, si es necesario
      setRegistrationMessage('Error al registrar el usuario. Por favor, intenta nuevamente.');
    }
  };

  return (
    <div className="registrase">
      <div className="container-registro-wrapper">
        <div className="container-registro">
          <img className="rectangle" alt="Rectangle" src={RegistroIMG} />
          <div className="text-wrapper">Registrarse</div>
          
          <form onSubmit={(event) => onSubmit(event)}>
            {/* Resto del código... */}

            <div className="overlap-2">
              <label htmlFor="password2" className="password"></label>
              <input
                type="password"
                id="password2"
                name="password2"
                placeholder="********"
                onChange={(event) => onInputChange(event)}
                value={formState.password2}
              />
            </div>

            {/* Resto del código... */}

            <button type="submit" className="btn-registrarse">
              <div className="overlap-group-2">
                <span className="text-wrapper-7">Registrarse</span>
                <div className="rectangle-2"></div>
              </div>
            </button>
          </form>

          {/* Mostrar el mensaje de registro */}
          {registrationMessage && <div className="registration-message">{registrationMessage}</div>}
        </div>
      </div>
    </div>
  );
};

export default Registro;
